    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed

        DefaultTableModel tb1Model = (DefaultTableModel) jTable2.getModel();
        if (jTable2.getSelectedRowCount() == 1) {
            String name = jTextField2.getText();
            String oldFees = jTextField3.getText();
            String NewFees = jTextField4.getText();
            tb1Model.setValueAt(name, jTable2.getSelectedRow(), 0);
            tb1Model.setValueAt(oldFees, jTable2.getSelectedRow(), 1);
            tb1Model.setValueAt(NewFees, jTable2.getSelectedRow(), 2);
        }
        JOptionPane.showMessageDialog(this, "The Fees has been Updated Succefully", "confirmation Message", JOptionPane.PLAIN_MESSAGE);

        DoctorForm s = new DoctorForm();
        s.setVisible(true);
        this.dispose();

    }//GEN-LAST:event_btnUpdateActionPerformed

