    private void jTable2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable2MouseClicked
        DefaultTableModel tb1Model = (DefaultTableModel) jTable2.getModel();
        String tb1Name = tb1Model.getValueAt(jTable2.getSelectedRow(), 0).toString();
        String tb1oldFees = tb1Model.getValueAt(jTable2.getSelectedRow(), 1).toString();
        String tb1NewFees = tb1Model.getValueAt(jTable2.getSelectedRow(), 2).toString();
        jTextField2.setText(tb1Name);
        jTextField3.setText(tb1oldFees);
        jTextField4.setText(tb1NewFees);
        // TODO add your handling code here:
    }//GEN-LAST:event_jTable2MouseClicked

